import React, { useState } from 'react';
import axios from 'axios';

const { exec } = require('child_process');

export default function Result() {

    const [postData, setPostData] = useState([]);
    const [resultData, setResultData] = useState([]);
    const APIurl = 'https://naveropenapi.apigw.ntruss.com/text-summary/v1/summarize'

    let finalBody = '';

    const APIpath = './SummaryAPI.py';
    const content = finalBody;

    const handleGetData = async () => {
        try {
            const response = await (await axios.get(`http://43.202.79.6:3001/result?paperId=2`)).data;
            const extractedBodies = response.map(item => item.body);
            finalBody = extractedBodies.join('\n');
            setPostData(finalBody);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleGetResultData = async () => {
        console.log('요약기 작동함');
        try {
            const command = `python ${APIpath} "${content}"`;

            exec(command, (error, stdout, stderr) => {
                if (error) {
                    console.error(`Error executing Python script: ${error.message}`);
                    return;
                }

                // Python 스크립트의 결과 출력
                console.log(`Python script output: ${stdout}`);
                setResultData(JSON.parse(stdout));  // Assuming the Python script returns JSON
            });
        } catch (error) {
            console.error('Error fetching result data: ', error);
        }
    };

  return (
    <div>
      <h2>포스트 결과 종합</h2>
      <button onClick={handleGetData}>내 자료 가져오기</button>
      <button onClick={handleGetResultData}>한 줄 요약 보기</button>
      
      {/* 받아온 데이터를 출력 */}
      {postData && (
        <div>
          <h3>서버에서 받아온 데이터:</h3>
          <pre>{JSON.stringify(postData, null, 2)}</pre>
        </div>
      )}

      {resultData && (
        <div>
            <h3>서버 데이터 한 줄 요약</h3>
            <pre>{JSON.stringify(resultData, null, 2)}</pre>
        </div>
      )}

    </div>
  );
}
