import React, { useState } from 'react';
import axios from 'axios';

export default function Result() {
  const [postData, setPostData] = useState('');
  const [summary, setSummary] = useState('');

  const client_id = "mdyy1cdvly";
  const client_secret = "32GVEkD8L7xwRoaMnRgGnLbTVZJ87tyo7M6Blt46";
  const clovaApiUrl = '/proxy/naveropenapi.apigw.ntruss.com/text-summary/v1/summarize';

  //const serverIpAddress = '43.202.79.6'
  const serverIpAddress = 'localhost'
  var finalBody = ''

  const handleGetData = async () => {
    try {
      const response = await axios.get(`http://${serverIpAddress}:3001/result?paperId=2`);
      const extractedBodies = response.data.map(item => item.body);
      finalBody = extractedBodies.join('\n');
      setPostData(finalBody);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleSummary = async () => {
    const response = await axios.post(`http://${serverIpAddress}:3001/api`);
    const jsonString = JSON.stringify(response.data.summary);
    setSummary(jsonString)
    console.log(typeof jsonString);
  };

  return (
    <div>
      <h2>포스트 결과 종합</h2>
      <button onClick={handleGetData}>내 자료 가져오기</button>
      <button onClick={handleSummary}>한 줄 요약 보기</button>

      {/* 받아온 데이터를 출력 */}
      {postData && (
        <div>
          <h3>서버에서 받아온 데이터:</h3>
          <pre>{postData}</pre>
        </div>
      )}

      {summary && (
        <div>
          <h3>서버 데이터 한 줄 요약</h3>
          <pre>{summary}</pre>
        </div>
      )}
    </div>
  );
}
