import React, { useState } from 'react';
import axios from 'axios';

export default function Result() {
  const [postData, setPostData] = useState('');
  const [summary, setSummary] = useState('');

  const client_id = "mdyy1cdvly";
  const client_secret = "32GVEkD8L7xwRoaMnRgGnLbTVZJ87tyo7M6Blt46";
  const clovaApiUrl = 'https://naveropenapi.apigw.ntruss.com/text-summary/v1/summarize';

  const handleGetData = async () => {
    try {
      const response = await axios.get('http://43.202.79.6:3001/result?paperId=2');
      const extractedBodies = response.data.map(item => item.body);
      const finalBody = extractedBodies.join('\n');
      setPostData(finalBody);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleClovaSummary = async () => {
  try {
    const client_id = "mdyy1cdvly";
    const client_secret = "32GVEkD8L7xwRoaMnRgGnLbTVZJ87tyo7M6Blt46";

    const headers = {
      'X-NCP-APIGW-API-KEY-ID': client_id,
      'X-NCP-APIGW-API-KEY': client_secret,
      'Content-Type': 'application/json',
    };

    const language = 'ko';
    const model = 'news';
    const tone = '2';
    const summaryCount = '3';

    const requestData = {
      document: {
        title: 'Title placeholder',
        content: postData,
      },
      option: {
        language,
        model,
        tone,
        summaryCount,
      },
    };

    const response = await axios.post('https://naveropenapi.apigw.ntruss.com/text-summary/v1/summarize', requestData, { headers });

    if (response.status === 200) {
      setSummary(response.data.summary);
    } else {
      console.error('Error calling CLOVA Summary API:', response.statusText);
    }
  } catch (error) {
    console.error('Error calling CLOVA Summary API:', error.message);
  }
};


  return (
    <div>
      <h2>포스트 결과 종합</h2>
      <button onClick={handleGetData}>내 자료 가져오기</button>
      <button onClick={handleClovaSummary}>한 줄 요약 보기</button>

      {/* 받아온 데이터를 출력 */}
      {postData && (
        <div>
          <h3>서버에서 받아온 데이터:</h3>
          <pre>{postData}</pre>
        </div>
      )}

      {summary && (
        <div>
          <h3>서버 데이터 한 줄 요약</h3>
          <pre>{summary}</pre>
        </div>
      )}
    </div>
  );
}
