import React, { useState } from 'react';
import axios from 'axios';

const { spawn } = require('child_process');
const result = spawn('python', ['SummaryAPI.py']);

export default function Result() {

    const [postData, setPostData] = useState([]);
    const [resultData, setResultData] = useState([]);

    let finalBody = '';


    const handleGetData = async () => {
        try {
            const response = await axios.get('http://43.202.79.6:3001/result?paperId=2');
            const extractedBodies = response.data.map(item => item.body);
            finalBody = extractedBodies.join('\n');
            setPostData(finalBody);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleGetResultData = async () => {
        console.log('요약기 작동함');
        result.stdout.on('data', function(data) {
            console.log(data.toString());
        });
    };

    return (
        <div>
            <h2>포스트 결과 종합</h2>
            <button onClick={handleGetData}>내 자료 가져오기</button>
            <button onClick={handleGetResultData}>한 줄 요약 보기</button>

            {/* 받아온 데이터를 출력 */}
            {postData && (
                <div>
                    <h3>서버에서 받아온 데이터:</h3>
                    <pre>{JSON.stringify(postData, null, 2)}</pre>
                </div>
            )}

            {resultData && (
                <div>
                    <h3>서버 데이터 한 줄 요약</h3>
                    <pre>{JSON.stringify(resultData, null, 2)}</pre>
                </div>
            )}

        </div>
    );
}
