import React, { useState } from 'react';
import axios from 'axios';

export default function Result() {
  const [postData, setPostData] = useState('');
  const [summary, setSummary] = useState('');;

  const handleGetData = async () => {
    try {
      const response = await axios.get('http://43.202.79.6:3001/result?paperId=2');
      const extractedBodies = response.data.map(item => item.body);
      const finalBody = extractedBodies.join('\n');
      setPostData(finalBody);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

    const handleClovaSummary = async () => {
        const { exec } = require('child_process');

        // 실행할 파이썬 스크립트
        const pythonScript = 'SummaryAPI.py';

        // 파이썬 코드 실행
        const child = exec(`python ${pythonScript}`, (error, stdout, stderr) => {
        if (error) {
            console.error(`실행 중 오류 발생: ${error}`);
            return;
        }
        console.log(`표준 출력: ${stdout}`);
        console.error(`표준 에러 출력: ${stderr}`);
        });

        // 파이썬 코드 실행이 완료되면 이벤트 처리
        child.on('close', (code) => {
        console.log(`파이썬 코드 실행 종료, 종료 코드: ${code}`);
        });

  };
  

  return (
    <div>
      <h2>포스트 결과 종합</h2>
      <button onClick={handleGetData}>내 자료 가져오기</button>
      <button onClick={handleClovaSummary}>한 줄 요약 보기</button>

      {/* 받아온 데이터를 출력 */}
      {postData && (
        <div>
          <h3>서버에서 받아온 데이터:</h3>
          <pre>{postData}</pre>
        </div>
      )}

      {summary && (
        <div>
          <h3>서버 데이터 한 줄 요약</h3>
          <pre>{summary}</pre>
        </div>
      )}
    </div>
  );
}
